import React, { useEffect, useState } from 'react';

export default function About() {
  const baseStreak = 1381;
  const [streak, setStreak] = useState(baseStreak);

  useEffect(() => {
    const baseStreak = 1380;
    const storedStreak = localStorage.getItem('duoStreak');
    const lastUpdate = localStorage.getItem('duoStreakLastUpdate');
    const now = new Date();

    if (storedStreak && lastUpdate) {
      const last = new Date(lastUpdate);

      const today = new Date(now.getFullYear(), now.getMonth(), now.getDate());
      const lastDay = new Date(last.getFullYear(), last.getMonth(), last.getDate());

      const daysPassed = Math.floor((today - lastDay) / (1000 * 60 * 60 * 24));

      if (daysPassed > 0) {
        const newStreak = parseInt(storedStreak) + daysPassed;
        setStreak(newStreak);
        localStorage.setItem('duoStreak', newStreak);
        localStorage.setItem('duoStreakLastUpdate', now.toISOString());
      } else {
        setStreak(parseInt(storedStreak));
      }
    } else {
      setStreak(baseStreak);
      localStorage.setItem('duoStreak', baseStreak);
      localStorage.setItem('duoStreakLastUpdate', now.toISOString());
    }
  }, []);


  return (
    <section className="py-5 bg-light text-dark" id="about">
      <div className="container">
        <h2 className="text-center fw-bold mb-4">About Me</h2>
        <p className="lead">
          I'm <strong>Christian Thomas</strong>, a dedicated educator turned full-stack developer.
          For seven years, I taught at Lindenwold Middle School and coached student-athletes in soccer,
          wrestling, and track. While I’ve always enjoyed mentoring and leading, my passion for problem-solving
          led me to pivot into software development.
        </p>
        <p>
          Since 2022, I’ve immersed myself in tech — completing an intensive full-stack program through
          Zip Code Wilmington and building projects using Java, Spring Boot, MySQL, and React.
        </p>
        <p>
          I’m also committed to lifelong learning — I’ve maintained a <strong>{streak}-day Duolingo streak</strong>
          while studying Spanish daily
        </p>
      </div>
    </section>
  );
}
